import {  Outlet, NavLink } from "react-router-dom";
import styles from './Root.module.css'

export const ROUTES = {
    CONTACTS: "/contacts",
    APPOINTMENTS: "/appointments",
  };

function Root() {
    const handleClick = ()=>{
       const contact = document.getElementById('contact');
       const appointment = document.getElementById('appointment');
       if(contact){
        contact.style.background="#db504a";
        console.log('working')
       }else if(appointment){
        appointment.style.background ="#db504a";
       }else{
        return;
       }
    }

    return (
        <>
            <header className={styles.header}>PLANNER</header>
            <nav className={styles.nav}>
                <NavLink  className={styles.text} to={ROUTES.CONTACTS} >
                <div id="contact" onClick={handleClick} className={styles.item}>Contacts</div>
                </NavLink>
                <NavLink className={styles.text} to={ROUTES.APPOINTMENTS} >
                <div id="appointment" onClick={handleClick} className={styles.item}>Appointments</div>
                </NavLink>
            </nav>
            <Outlet/>
      </>
    );

}

export default Root;
