import {  Outlet, NavLink } from "react-router-dom";
import styles from './Root.module.css'

export const ROUTES = {
    CONTACTS: "/contacts",
    APPOINTMENTS: "/appointments",
  };

function Root() {
    const handleClick = ()=>{
       const el = document.getElementsByClassName('div');
       let x = 0;
       if(el[x]){
        el[x].style.background="db504a";
        console.log('working')
       }else if(el[1]){
        el[1].style.background ="db504a";
       }else{
        return;
       }
    }

    return (
        <>
            <header className={styles.header}>PLANNER</header>
            <nav className={styles.nav}>
                <NavLink className={styles.text} to={ROUTES.CONTACTS} >
                <div onClick={handleClick} className={styles.item}>Contacts</div>
                </NavLink>
                <NavLink className={styles.text} to={ROUTES.APPOINTMENTS} >
                <div onClick={handleClick} className={styles.item}>Appointments</div>
                </NavLink>
            </nav>
            <Outlet/>
      </>
    );

}

export default Root;
